mapper.insert:
INSERT INTO matflix (
    mf_id, mf_pw, mf_pw_chk, mf_nickname,
    mf_name, mf_email, mf_phone, mf_birth, mf_gender
) VALUES (
    #{mf_id}, #{mf_pw}, #{mf_pw_chk}, #{mf_nickname},
    #{mf_name}, #{mf_email}, #{mf_phone}, #{mf_birth}, #{mf_gender}
)

mapper.select:
SELECT COUNT(*) FROM matflix


		AND mf_id = #{mf_id}


		AND mf_pw = #{mf_pw}

mapper.update:
update matflix set mf_pw=#{mf_pw}, mf_pw_chk=#{mf_pw_chk},
mf_name=#{mf_name}, mf_email=#{mf_email}

	mf_id = #{mf_id}
